import React, { createContext, useContext, useState } from 'react';

const UserStateContext = createContext();

export const useUserStateContext = () => {
	const context = useContext(UserStateContext);

	if (context === undefined) {
		throw new Error('userUserStateContext must be used within a UserStateProvider');
	}

	return context;
};

export const UserStateProvider = ({ children }) => {

	const [workingUser, setWorkingUser] = useState({});
	const [loggedInUser, loginUser] = useState({});
	const [isLoggedIn, setIsLoggedIn] = useState(undefined);
	const [formFields, setFormFields] = useState({});
	const [isLandingPage, setIsLandingPage] = useState(true);
	const [loginError, setLoginError] = useState(null);
	const [imageList, setImageList] = useState([]);

	const clickOffLanding = () => {
		setIsLandingPage(false);
	}

	const setLoggedInUser = (user) => {
		loginUser(user);
		setWorkingUser(user);
	}

	const handleLogout = async () => {
		setLoggedInUser({});
		setIsLoggedIn(false);
		setWorkingUser(null);
		localStorage.clear();
		return 'SUCCESSFULLY_LOGGED_OUT';
	};

	return (
		<UserStateContext.Provider
			value={{
				isLoggedIn,
				formFields,
				workingUser,
				isLandingPage,
				loginError,
				loggedInUser,
				imageList,
				setLoginError,
				setFormFields,
				handleLogout,
				clickOffLanding,
				setWorkingUser,
				setIsLoggedIn,
				setIsLandingPage,
				setFormFields,
				setLoggedInUser,
				setImageList
			}}
		>
			{children}
		</UserStateContext.Provider>
	)
};
